import React, { useState } from "react";
import axios from "axios";
import "./CreateResturant.css";

const CreateRestaurant = () => {
  const url = "http://localhost:3000/restaurant"; 

  const [data, setData] = useState({
    name: "",
    cuisine: "",
    location: "",
    image: "",
  });

  function submit(e) {
    e.preventDefault();
    axios
      .post(url, {
        name: data.name,
        cuisine: data.cuisine,
        location: data.location,
        image: data.image,
      })
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }

  function handle(e) {
    const newdata = { ...data };
    newdata[e.target.id] = e.target.value;
    setData(newdata);
    console.log(newdata);
  }

  return (
    <div className="cover">
      <form onSubmit={(e) => submit(e)}>
        <input
          type="text"
          onChange={(e) => handle(e)}
          id="name"
          value={data.name}
          placeholder="Name"
        />
        <input
          type="text"
          onChange={(e) => handle(e)}
          id="cuisine"
          value={data.cuisine}
          placeholder="CuisineType"
        />
        <input
          type="text"
          onChange={(e) => handle(e)}
          id="location"
          value={data.location}
          placeholder="Location"
        />
        <label htmlFor="image">Select Image</label>
        <input
          type="file"
          onChange={(e) => handle(e)}
          id="image"
          value={data.image}
          name="image"
          accept="image/*"
        />
        <div className="login-btn" onClick={(e) => submit(e)}>
          Create Restaurant
        </div>
      </form>
    </div>
  );
};

export default CreateRestaurant;
