import React, { useState } from "react";
import "./CreateResturant.css"
import { Axios } from "axios";

const CreateResturant = () =>{
    const url =""
    const [data, setData] = useState({
        name:"",
        cuisine:"",
        location:"",
        image:""
    })

    function submit(e){
        e.preventDefault();
        Axios.post(url,{
            name:data.name,
            cuisine:data.cuisine,
            location:data.location,
            image:data.image            
        })
            .then(res=>{
                console.log(res.data)
              })
    }

    function handle(e){
        const newdata={...data}
        newdata[e.target.id]=e.target.value
        setData(newdata)
        console.log(newdata)
    }


    return (
        <div className="cover">
            <form onSubmit={(e)=> submit(e)}>
            <input type="text" onChange={(e)=>handle(e)} id="name" value={data.name} placeholder="Name"/>
            <input type="text" onChange={(e)=>handle(e)} id="cuisine" value={data.cuisine} placeholder="CuisineType"/>
            <input type="text" onChange={(e)=>handle(e)} id="location" value={data.location} placeholder="Location"/>
            <label for="image">Select Image</label>
            <input type="file" onChange={(e)=>handle(e)} id="image" value={data.image} name="image" accept="image/*"/>
            <div className="login-btn">Create Restaurant</div>
            </form>
        </div>
    )
}

export default CreateResturant
